import React, { Component } from 'react';
import Sirves_Type from '../../td_file/Sirves_Type';
import { getServiess,addServies } from '../../url/const';
export class SirvesType extends Component {



    constructor(){
        super()
        this.state={
          error: null,
          isLoaded: false,
          items: []
        }
      }


      async  componentDidMount(){


        const response = await fetch(getServiess);
          const json = await response.json();
          this.setState({ items: json });
             console.log(this.state.items);


          }
  render() {
    return (
      <div className='head' id="ans">

        <h1 className='title'>Service</h1>

            <div class="row_sre">
                {this.state.items.map((e)=>{
                          return <Sirves_Type 
                          id={e['_id']} title={e['title']} img={e['type']} body={e['body']} firstImages={e['firstImages']} firstTitle={e['firstTitle']} firstBody={e['firstBody']} secondImages={e['secondImages']} secondTitle={e['secondTitle']} secondBody={e['secondBody']} thirdTitle={e['thirdTitle']} thirdBody={e['thirdBody']} fourthTitle={e['fourthTitle']} fourthBody={e['fourthBody']} perks={e['perks']} firstPerkBody={e['firstPerkBody']} secondPerk={e['secondPerk']} firstPerkCircle={e['firstPerkCircle']} secondPerkCircle={e['secondPerkCircle']} fifth={e['fifth']} 
                           />
                        })}
            </div>






<h1 className='title'>edit page</h1>
<table>
     <tr>  
     <th>ServesType</th>
     </tr>
     <tr>
        <td>
            <table>
                <tr>
                    <th>block Id</th>
                    <th> title</th>
                    <th> img</th>
                    <th> body</th>
                </tr>

                <tr>
                <td> <span className='flex'><input  className='input' onChange={e => this.setState({ id: e.target.value })}></input></span> </td>
                    <td> <span className='flex'><input  className='input' onChange={e => this.setState({ title: e.target.value })}></input></span> </td>
                    <td> <span className='flex'><input  className='input' onChange={e => this.setState({ img: e.target.value })}></input></span> </td>
                    <td> <span className='flex'><input  className='input' onChange={e => this.setState({ body: e.target.value })}></input></span> </td>


                </tr>
            </table>
        </td>
     </tr>
     <button className="save" onClick={()=>{
          const body=JSON.stringify({
            body:this.state.body,
            title: this.state.title,
            type:this.state.img,
          })

          console.log(body);

      const json = fetch(addServies,{
        method:'post',
        headers: {
          'Content-Type': 'application/json'
      },
        body:body,
      },(res)=>{console.log(res);})
        }}>save</button>
</table>

      </div>
    );
  }
}

export default SirvesType;
