import React, { Component } from "react";
import Spic_field from "../spic_field";
import { DR_getAllDr,DR_addDr,DR_updateDoctor,DR_id_getDoctor  } from "../../url/const";
import Dr_list from "../../td_file/Dr_list";

export class Doctor extends Component {
  constructor(){
    super()
    this.state={
      num:1,
      fields:[1],
      error: null,
      isLoaded: false,
      items: [],


    }
  }

async  componentDidMount(){


  const response = await fetch(DR_getAllDr);
    const json = await response.json();
    this.setState({ items: json });
       console.log(json);


    }


  render() {

   const addFild=()=> {
 this.state.fields.push(this.state.fields.length+1)
      // const td = document.createElement("tr");
      // td.innerHTML = ` <td> <span class="flex"><input id='hi${this.state.num}' class='input'></span> </td>`;
      // this.setState({num:this.state.num+1})
      // document.querySelector("#hear").appendChild(td);

  this.setState({
    num:this.state.num+1
  })
    console.log(this.state.fields);
    }
    return (
      <div className="head" id="ans">

        <h1 className="title">DR list</h1>
        <table>
          <tr>
            <th>DR Id</th>
            <th>DR name</th>
            <th>DR title</th>
            <th>DR img</th>

          </tr>
          <tr>
            <td>
              <span className="flex">
                <input className="input" onChange={((e)=>{this.setState({
                  id:e.target.value
                })})} ></input>
              </span>
            </td>

            <td>
              <span className="flex">
                <input id="namein" className="input" defaultValue={e => this.setState({ name: e.target.value })} onChange={e => this.setState({ name: e.target.value })}></input>
              </span>
            </td>

            <td>
              <span className="flex">
                <input className="input" onChange={e => this.setState({ title: e.target.value })}></input>
              </span>
            </td>

            <td>
              <span className="flex">
                <input className="input" onChange={e => this.setState({ img: e.target.value })}></input>
              </span>
            </td>


          </tr>

          <tr>
            <th>DR location</th>
            <th>is male</th>
            <th>ratting num</th>
            <th>ratting</th>
          </tr>
          <tr>
          <td>
              <span className="flex">
                <input className="input" onChange={e => this.setState({ location: e.target.value })}></input>
              </span>
            </td>

            <td>
              <span className="flex">
                <input  id="_checkbox" type={'checkbox'} onChange={(e) => {this.setState({ is_male: e.target.checked  })
                console.log(e);}}></input>
                <label for="_checkbox">
  <div id="tick_mark"></div>
</label>

              </span>
            </td>

            <td>
              <span className="flex">
                <input className="input" type={"number"} onChange={e => this.setState({ rattingNumber: e.target.value })}></input>
              </span>
            </td>

            <td>
              <span className="flex">
                <input className="input" type={"number"} onChange={e => this.setState({ ratting: e.target.value })}></input>
              </span>

            </td>
          </tr>

          <tr>
            <th>specialties</th>
            <th>body</th>
          </tr>
          <tr>
            <td>
            <table>
              <span className="flex1">
                <div id="hear">
                  <tr>
                    <th>
                      <button
                        className="add_but"
                        onClick={() => {
                          addFild();
                        }}
                      >
                        add fild
                      </button>
                    </th>
                  </tr>
                  <tr>

                  </tr>
                  {this.state.fields.map((e,index)=>{

                          return <tr> <td> <span class="flex"><input id={`input_num${e}`}  className='input' onChange={(val)=>{


                         this.state.spic[index]? this.state.spic[index]=val.target.value:   this.state.spic.push(val.target.value)

                            console.log(this.state.spic);
                          }}/></span>
                           </td>
                            </tr>
                        })}
                </div>
              </span>
              </table>
            </td>
            <td>
              <span className="flex">
              <input className="input" onChange={e => {this.setState({ body: e.target.value })
                    console.log(this.state.body);}}></input>
              </span>
            </td>
          </tr>
                  <button className="save" onClick={()=>{


          var body=JSON.stringify({
            id:this.state.id,
            Specialties: this.state.spic,
            body:this.state.body??this.state.items.body,
            isMale:this.state.is_male,
            jobTitle:this.state.title,
            location:this.state.location,
            name: this.state.name.replace('d',`s`),
            profileImage:this.state.img,
            ratting: this.state.ratting,
            rattingNumber:this.state.rattingNumber
          })
     if(!this.state.id){ const json = fetch(DR_addDr,{
        method:'post',
        headers:{
          'Content-Type': 'application/json'
      },
        body:body,
      },(res)=>{console.log(res);})
    }else{
      const json = fetch(DR_updateDoctor(this.state.id),{
        method:'PATCH',
        headers: {
          'Content-Type': 'application/json'
      },
      body:body,
      },(res)=>{console.log(res);})
    }


        }}>save</button>
        </table>


        <h1 className="title">DR list</h1>
        <table>
          <tr>
            <th>Id</th>
            <th>name</th>
            <th>title</th>
            <th>is male</th>
            <th>more information</th>
            <th>remove</th>
          </tr>
          {this.state.items.map((e)=>{
                          return <Dr_list 
                          id={e['_id']} name={e['name']} title={e['jobTitle']} img={e['profileImage']} location={e["location"]} is_male={e["isMale"]} R_num={e['rattingNumber']}  ratting={e['ratting']} specialties={e['Specialties']} body={e['body']}
                           />
                        })}
        </table>


      </div>

    );
  }
}

export default Doctor;
