import React, { Component } from 'react';
import MeerOur_Team from '../../td_file/MeerOur_Team';
import { addTeam,getTeams,updateTeam } from '../../url/const';

export class MeerOurTeam extends Component {

    constructor(){
        super()
        this.state={
          error: null,
          isLoaded: false,
          items: [],
          id:1
        }
      }



    async  componentDidMount(){


        const response = await fetch(getTeams);
          const json = await response.json();
          this.setState({ items: json });
             console.log(this.state.items);


          }
  render() {
    return (
      <div className='head' id="ans">
        <h1 className='title'>home page</h1>
        <h2 className='dataname'>meer our team</h2>

            <table>
                <tr>
                    <th> Id</th>
                    <th> name</th>
                    <th> img</th>
                    <th>title</th>
                    <th>remove</th>
                </tr>
                {this.state.items.map((e)=>{
                          return <MeerOur_Team 
                          id={e['_id']} name={e['name']} img={e['image']} title={e['title']} 
                           />
                        })}


</table>




<h1 className='title'>edit page</h1>
<table>
     <tr>  
     <th>meer our team</th>
     </tr>
     <tr>
        <td>
            <table>
                <tr>
                    <th>Id</th>
                    <th>name</th>
                    <th>img</th>
                    <th>title</th>
                </tr>

                <tr>
                    <td> <span className='flex'><input  className='input' onChange={
                      e => this.setState({ id: e.target.value })
                    }

                      ></input></span> </td>
                    <td> <span className='flex'><input  className='input' onChange={e => this.setState({ name: e.target.value })}></input></span> </td>
                    <td> <span className='flex'><input  className='input' onChange={e => this.setState({ img: e.target.value })}></input></span> </td>
                    <td> <span className='flex'><input  className='input' onChange={e => this.setState({ title: e.target.value })}></input></span> </td>
                    {console.log(this.state.id)}

                </tr>


            </table>
        </td>
     </tr>
     <button className="save" onClick={()=>{
      if(this.state.id="1"){
          const body=JSON.stringify({
            name:this.state.name,
            image:this.state.img,
            title:this.state.title,
            location:'none'
          })

          console.log(body);

      const json = fetch(addTeam,{
        method:'post',
        headers: {
          'Content-Type': 'application/json'
      },
        body:body,
      },(res)=>{console.log(res);})




        }if(this.state.id !="1"){
          const body=JSON.stringify({
            name:this.state.name,
            image:this.state.img,
            title:this.state.title,
            location:'none'
          })

          console.log(body);

      const json = fetch(updateTeam(this.state.id),{
        method:'post',
        headers: {
          'Content-Type': 'application/json'
      },
        body:body,

      },(res)=>{console.log(res);})









        }
        window.location.reload();}}>save</button>

</table>
      </div>
    );
  }
}

export default MeerOurTeam;
