import React, { Component } from 'react';
import Clients_Say from '../../td_file/Clients_Say';
import { addClient,getClients,updateClient } from '../../url/const';
export class ClientsSay extends Component {


    constructor(){
        super()
        this.state={
          error: null,
          isLoaded: false,
          items: []
        }
      }


      async  componentDidMount(){


        const response = await fetch(getClients);
          const json = await response.json();
          this.setState({ items: json });
             console.log(this.state.items);


          }
  render() {
    return (
      <div className='head' id="ans">
                <h1 className='title'>edit page</h1>
<table>
     <tr>  
     <th>ClientsSay</th>
     </tr>
     <tr>
        <td>
            <table>
                <tr>
                    <th>Clients Id</th>
                    <th>Clients name</th>
                    <th>Clients img</th>
                    <th>Clients body</th>
                </tr>

                <tr>
                <td> <span className='flex'><input  className='input' onChange={((e)=>{this.setState({id:e.target.value})})}></input></span> </td>
                    <td> <span className='flex'><input  className='input' onChange={e => this.setState({ name: e.target.value })}></input></span> </td>
                    <td> <span className='flex'><input  className='input' onChange={e => this.setState({ img: e.target.value })}></input></span> </td>
                    <td> <span className='flex'><input  className='input' onChange={e => this.setState({ body: e.target.value })}></input></span> </td>


                </tr>
            </table>
        </td>
     </tr>     <button className="save" onClick={()=>{
          const body=JSON.stringify({
            id:this.state.id,
            body:this.state.body,
            name: this.state.name,
            image:this.state.img,
          })
          if(!this.state.id){ const json = fetch(addClient,{
            method:'post',
            headers:{
              'Content-Type': 'application/json'
          },
            body:body,
          },(res)=>{console.log(res);})
        }else{
          const json = fetch(updateClient(this.state.id),{
            method:'PATCH',
            headers: {
              'Content-Type': 'application/json'
          },
          body:body,
          },(res)=>{console.log(res);})
        }

      window.location.reload();
        }}>save</button>
</table>




















        <h1 className='title'>home page</h1>
<table>
     <tr>  
     <th>ClientsSay</th>
     </tr>
     <tr>
        <td>
            <table>
                <tr>
                    <th>Clients Id</th>
                    <th>Clients name</th>
                    <th>Clients img</th>
                    <th>Clients body</th>
                    <th>remove</th>
                </tr>


                {this.state.items.map((e)=>{
                          return <Clients_Say 
                          id={e['_id']} name={e['name']} img={e['image']} body={e['body']}
                           />
                        })}
            </table>
        </td>
     </tr>
</table>
      </div>
    );
  }
}

export default ClientsSay;
