import React, { Component } from 'react';
import { deleteClient } from '../url/const';

export function Clients_Say({id,name,img,body}) {
    

    return (
        <>



        <tr id={id}>
            
                    <td> <span className='flex'>{id}</span> </td>
                    <td> <span className='flex'>{name}</span> </td>
                    <td> <span className='flex'>
                    <button
              className="show"
              onClick={() => {
                const div = document.createElement("div");
                div.className = "ans1";
                

                div.innerHTML = `
                        <div className='body_riv'>
                        <button id="back_but" onClick={document.querySelector('.ans1').remove()}>X</button>
                        <style>

                        .dr_body{
                            width:60%;
                            height: fit-content;
                            max-height: 70vh;
                            word-break: break-all;
                            padding:5% 20% 5% 20%;
                            overflow-y: scroll;
                        }
                        .flex_img{
                          display: flex;

                        }
                        .dr_img{
                          
                            display: flex;
                            justify-content: space-between;
                          }
                        .dr_img img{
                            width: 15vw;
                            height: fit-content;
                        }
                        .dr_img span{
                            width: fit-content;
                            margin-right:2vw ;
                            justify-content: center;
                            align-content: center;
                        }
                        .dr_img span h1{
                            font-size: 1.6vw;
                            color: #555;
                            display: flex;
                            justify-content: center;

                        }
                        .dr_img span ul{
                            display: flex;
                            justify-content: center;
                            align-content: center;
                            font-size: 1.6vw;
                            margin-top: 1vw;
                            color: #555;
                        }
                        .dr_img span ul svg{
                            margin-top: 0.59vw;
                            
                        }
                        .body_data_dr{
                          font-size:1.2vw;
                          color: #555;
                          width: 30vw;
                          

                          
                        }
                        .body_title_dr{
                          width:40vw;
                          padding:3%;
                          display: flex;
                          justify-content: center;
                          align-content: center;
                          font-size: 1.6vw;
                          box-shadow: rgba(50, 50, 93, 0.25) 0px 50px 3vw -20px, rgba(0, 0, 0, 0.3) 0px 30px 60px -30px, rgba(10, 37, 64, 0.35) 0px -2px 6px 0px inset;  margin-top: 3vh;
                          border-radius: 1vw;
                          
                        }
                        .dr_body::-webkit-scrollbar-track
{
	-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
	border-radius: 10px;
	background-color: #F5F5F5;
}

.dr_body::-webkit-scrollbar
{
	width: 12px;
	background-color: #F5F5F5;
}

.dr_body::-webkit-scrollbar-thumb
{
	border-radius: 10px;
	-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,.3);
	background-color: #555;
}

                    
                    
                    
                    
                      </style>
                    
                    <div class="dr_body">
                      <div class="dr_img">
                      <div>
                      <div class="body_title_dr">
                        <img src="${img}" alt="">

                        </div>
                        
                        <h1 class="body_title_dr">title</h1>
                        <p class="body_title_dr">${title}</p>

                        <h1 class="body_title_dr">body</h1>
                        <p class="body_title_dr">${body}</p>
                        

                        </div>
                      </div>

                    </div>

                      </div>`;
                document.querySelector("#ans").appendChild(div);
                document.getElementById("back_but").style.cssText = `
                        background-color: black;
                        font-size: 1.8vw;
                        width: 2vw;
                        height: 2vw;
                        color: #eee;
                        border: 0;
                        border-radius: 1vw;
                        

                        `;

              }}
            >
              show
            </button>
                      </span> </td>

        <td>
              <span className="rem">
                <button  onClick={ async()=>{
                     const response = await fetch(deleteClient(id),{
                        method:'delete'
                     }).then((e)=>{
                      if(e.status==200){
                        document.getElementById(id).remove();
                      }
                     });

                }}><svg xmlns="http://www.w3.org/2000/svg" fill="#000000" viewBox="0 0 26 26" width="2vw" height="2vw"><path d="M 11 -0.03125 C 10.164063 -0.03125 9.34375 0.132813 8.75 0.71875 C 8.15625 1.304688 7.96875 2.136719 7.96875 3 L 4 3 C 3.449219 3 3 3.449219 3 4 L 2 4 L 2 6 L 24 6 L 24 4 L 23 4 C 23 3.449219 22.550781 3 22 3 L 18.03125 3 C 18.03125 2.136719 17.84375 1.304688 17.25 0.71875 C 16.65625 0.132813 15.835938 -0.03125 15 -0.03125 Z M 11 2.03125 L 15 2.03125 C 15.546875 2.03125 15.71875 2.160156 15.78125 2.21875 C 15.84375 2.277344 15.96875 2.441406 15.96875 3 L 10.03125 3 C 10.03125 2.441406 10.15625 2.277344 10.21875 2.21875 C 10.28125 2.160156 10.453125 2.03125 11 2.03125 Z M 4 7 L 4 23 C 4 24.652344 5.347656 26 7 26 L 19 26 C 20.652344 26 22 24.652344 22 23 L 22 7 Z M 8 10 L 10 10 L 10 22 L 8 22 Z M 12 10 L 14 10 L 14 22 L 12 22 Z M 16 10 L 18 10 L 18 22 L 16 22 Z"/></svg></button>
              </span>
            </td>
        </tr>
        </>

    );
    
}
export default Clients_Say
